import { useState } from "react";
import "./App.css";
import BillCalculation from "./components/Bill/BillCalculation";
import Result from "./components/Bill/Result";
import Card from "./components/UI/Card/Card";

const App = () => {

  const [total, setTotal] = useState('0.00')
  const [tip, setTip] = useState('0.00')
  const billChangeHandler = (data) => {
   // console.log(data.total);
   console.log(data);
   setTotal(data.total)
   setTip(data.tip)
  }
  return (
    <Card>
      <BillCalculation onBillChange={billChangeHandler}></BillCalculation>
      <Result total={total} tip={tip}></Result>
    </Card>
  );
};

export default App;
