import { useState } from "react";
import Button from "../UI/Button/Button";
import classes from "./BillCalculation.module.css";
const BillCalculation = (props) => {


    const [bill, setBill] = useState('0.00')
    const [peopleCount, setPeopleCount] = useState('1')
    const [tipPercentage, setTipPercentage] = useState('0')

    const billHandler = (event) =>{
        setBill(event.target.value)
        const data = {
            total: event.target.value,
            tip: (Number(event.target.value)*(Number(tipPercentage))).toString()
        }

        props.onBillChange(data)
        console.log(data.total);
        console.log(bill);
    }

    const tipPercentageHandler = (event) => {
      setTipPercentage(event.target.value)
    }



  return (
    <form className={classes.bill}>
      <label>Bill</label>
      <input  onChange={billHandler} type="number" value={bill} step="0.01" min="0"></input>
      <label>Select Tip %</label>
      <div className={classes.buttons}>
        <Button value="0.05" onClick={tipPercentageHandler}>5%</Button>
        <Button value="0.1" onClick={tipPercentageHandler}>10%</Button>
        <Button value="0.15" onClick={tipPercentageHandler}>15%</Button>
        <Button value="0.25" onClick={tipPercentageHandler}>25%</Button>
        <Button value="0.50" onClick={tipPercentageHandler}>50%</Button>
        <Button>Custom</Button>
      </div>
      <label>Number of People</label>
      <input onChange={billHandler} type="number" value={peopleCount}></input>
    </form>
  );
};

export default BillCalculation;
